INSERT INTO products (name, price, image, detail_desc, short_desc, quantity, sold, factory, target) VALUES
('MacBook Air M3 13-inch', 28990000, 'macbook-air-m3-13.jpg', 'MacBook Air M3 13-inch với chip Apple M3 mới nhất, màn hình Liquid Retina 13.6 inch, pin 18 giờ, thiết kế siêu mỏng nhẹ chỉ 1.24kg. Tích hợp camera FaceTime HD 1080p, âm thanh không gian, bàn phím Magic Keyboard với Touch ID.', 'Laptop Apple siêu mỏng nhẹ với chip M3 mạnh mẽ, phù hợp cho sinh viên và doanh nhân', 50, 12, 'Apple', 'Sinh viên'),
('MacBook Pro M3 14-inch', 52990000, 'macbook-pro-m3-14.jpg', 'MacBook Pro M3 14-inch với chip Apple M3 Pro, màn hình Liquid Retina XDR 14.2 inch, GPU 18-core, RAM 18GB, SSD 512GB. Hỗ trợ tối đa 2 màn hình ngoài, pin 18 giờ, cổng Thunderbolt 4, MagSafe 3.', 'Laptop chuyên nghiệp cho đồ họa và thiết kế với màn hình XDR sắc nét', 35, 8, 'Apple', 'Đồ họa'),
('MacBook Air M2 15-inch', 32990000, 'macbook-air-m2-15.jpg', 'MacBook Air M2 15-inch với màn hình Liquid Retina 15.3 inch, chip M2 8-core CPU, GPU 10-core, RAM 8GB, SSD 256GB. Thiết kế siêu mỏng 11.5mm, trọng lượng 1.51kg, pin 18 giờ, 4 loa stereo.', 'Laptop Apple màn hình lớn 15 inch, lý tưởng cho công việc văn phòng', 40, 15, 'Apple', 'Văn phòng'),
('MacBook Pro M3 Max 16-inch', 89990000, 'macbook-pro-m3-max-16.jpg', 'MacBook Pro M3 Max 16-inch với chip M3 Max 12-core CPU, GPU 30-core, RAM 36GB, SSD 1TB. Màn hình Liquid Retina XDR 16.2 inch, hỗ trợ tối đa 4 màn hình ngoài, pin 22 giờ, cổng Thunderbolt 4.', 'Laptop Apple mạnh nhất cho các doanh nhân và chuyên gia cao cấp', 20, 3, 'Apple', 'Doanh nhân'),
('MacBook Air M1 13-inch', 22990000, 'macbook-air-m1-13.jpg', 'MacBook Air M1 13-inch với chip Apple M1, màn hình Retina 13.3 inch, RAM 8GB, SSD 256GB. Thiết kế fanless không quạt, pin 18 giờ, Touch ID, camera FaceTime HD 720p, trọng lượng 1.29kg.', 'Laptop Apple giá tốt với chip M1 ổn định, phù hợp đa năng', 60, 25, 'Apple', 'Đa năng'),
('MacBook Pro M2 13-inch', 35990000, 'macbook-pro-m2-13.jpg', 'MacBook Pro M2 13-inch với chip Apple M2, màn hình Retina 13.3 inch, Touch Bar, RAM 8GB, SSD 256GB. Hỗ trợ tối đa 2 màn hình ngoài, pin 20 giờ, cổng Thunderbolt 4, Touch ID.', 'Laptop Apple chuyên nghiệp cho sinh viên ngành kỹ thuật', 45, 10, 'Apple', 'Sinh viên'),
('MacBook Pro M3 Pro 14-inch', 62990000, 'macbook-pro-m3-pro-14.jpg', 'MacBook Pro M3 Pro 14-inch với chip M3 Pro 11-core CPU, GPU 14-core, RAM 18GB, SSD 512GB. Màn hình Liquid Retina XDR, hỗ trợ ProRes, ProRAW, pin 18 giờ, cổng HDMI, thẻ SD.', 'Laptop Apple cao cấp cho công việc đồ họa chuyên nghiệp', 25, 5, 'Apple', 'Đồ họa'),
('MacBook Air M2 13-inch', 29990000, 'macbook-air-m2-13.jpg', 'MacBook Air M2 13-inch với chip Apple M2, màn hình Liquid Retina 13.6 inch, camera FaceTime HD 1080p, RAM 8GB, SSD 256GB. Thiết kế mỏng nhẹ, pin 18 giờ, 4 màu sắc lựa chọn.', 'Laptop Apple hiện đại cho doanh nhân trẻ năng động', 55, 18, 'Apple', 'Doanh nhân'),
('MacBook Pro M1 Pro 16-inch', 59990000, 'macbook-pro-m1-pro-16.jpg', 'MacBook Pro M1 Pro 16-inch với chip M1 Pro 10-core CPU, GPU 16-core, RAM 16GB, SSD 512GB. Màn hình Liquid Retina XDR 16.2 inch, camera FaceTime HD 1080p, hệ thống âm thanh 6 loa.', 'Laptop Apple đa năng với hiệu suất cao cho mọi công việc', 30, 7, 'Apple', 'Đa năng'),
('MacBook Air M1 13-inch Gold', 24990000, 'macbook-air-m1-gold.jpg', 'MacBook Air M1 13-inch phiên bản Gold với chip Apple M1, màn hình Retina 13.3 inch, RAM 8GB, SSD 512GB. Thiết kế sang trọng, pin 18 giờ, Touch ID, camera FaceTime HD.', 'Laptop Apple phiên bản vàng sang trọng cho sinh viên', 35, 12, 'Apple', 'Sinh viên');
INSERT INTO products (name, price, image, detail_desc, short_desc, quantity, sold, factory, target) VALUES
('Dell XPS 13 Plus', 35990000, 'dell-xps-13-plus.jpg', 'Dell XPS 13 Plus với Intel Core i7-1360P, RAM 16GB LPDDR5, SSD 512GB, màn hình 13.4 inch FHD+ InfinityEdge. Thiết kế premium với bàn phím capacitive, touchpad haptic, cổng Thunderbolt 4.', 'Laptop Dell cao cấp siêu mỏng nhẹ cho doanh nhân hiện đại', 40, 15, 'Dell', 'Doanh nhân'),
('Dell Inspiron 15 3000', 12990000, 'dell-inspiron-15-3000.jpg', 'Dell Inspiron 15 3000 với AMD Ryzen 5 5500U, RAM 8GB DDR4, SSD 256GB, màn hình 15.6 inch FHD. Thiết kế đơn giản, bàn phím số, webcam HD, pin 3-cell, cổng USB-A, HDMI.', 'Laptop Dell giá rẻ phù hợp cho sinh viên với cấu hình ổn định', 80, 35, 'Dell', 'Sinh viên'),
('Dell Vostro 15 3000', 15990000, 'dell-vostro-15-3000.jpg', 'Dell Vostro 15 3000 với Intel Core i5-1135G7, RAM 8GB DDR4, SSD 512GB, màn hình 15.6 inch FHD anti-glare. Thiết kế doanh nghiệp, bàn phím số, webcam HD, cổng đa dạng.', 'Laptop Dell dành cho văn phòng với thiết kế chuyên nghiệp', 60, 28, 'Dell', 'Văn phòng'),
('Dell G15 5530 Gaming', 25990000, 'dell-g15-5530.jpg', 'Dell G15 5530 Gaming với Intel Core i7-13650HX, RTX 4050 6GB, RAM 16GB DDR5, SSD 512GB, màn hình 15.6 inch FHD 120Hz. Hệ thống tản nhiệt Alienware-inspired, bàn phím RGB.', 'Laptop Dell gaming với RTX 4050 mạnh mẽ cho game thủ', 45, 22, 'Dell', 'Gaming'),
('Dell Precision 5570', 52990000, 'dell-precision-5570.jpg', 'Dell Precision 5570 với Intel Core i7-12700H, RTX A2000 8GB, RAM 32GB DDR5, SSD 1TB, màn hình 15.6 inch 4K OLED. Workstation chuyên nghiệp, chuẩn ISV, bàn phím backlit.', 'Laptop Dell workstation chuyên nghiệp cho đồ họa 3D', 25, 8, 'Dell', 'Đồ họa'),
('Dell Latitude 7430', 42990000, 'dell-latitude-7430.jpg', 'Dell Latitude 7430 với Intel Core i7-1265U, RAM 16GB LPDDR5, SSD 512GB, màn hình 14 inch FHD. Thiết kế bền bỉ chuẩn quân đội, bảo mật cao, pin 4-cell, cổng Thunderbolt 4.', 'Laptop Dell bảo mật cao cho doanh nhân và cơ quan', 35, 12, 'Dell', 'Doanh nhân'),
('Dell Inspiron 16 Plus', 28990000, 'dell-inspiron-16-plus.jpg', 'Dell Inspiron 16 Plus với Intel Core i7-12700H, RTX 3050 Ti 4GB, RAM 16GB DDR5, SSD 512GB, màn hình 16 inch 2.5K. Thiết kế creator, hỗ trợ đa tác vụ, cổng đa dạng.', 'Laptop Dell đa năng với màn hình lớn cho mọi công việc', 50, 20, 'Dell', 'Đa năng'),
('Dell Vostro 14 5000', 18990000, 'dell-vostro-14-5000.jpg', 'Dell Vostro 14 5000 với Intel Core i5-1235U, RAM 8GB DDR4, SSD 256GB, màn hình 14 inch FHD. Thiết kế mỏng nhẹ, webcam HD, reader vân tay, pin 3-cell, cổng USB-C.', 'Laptop Dell văn phòng nhỏ gọn với hiệu suất tốt', 65, 30, 'Dell', 'Văn phòng'),
('Dell Inspiron 14 5000', 16990000, 'dell-inspiron-14-5000.jpg', 'Dell Inspiron 14 5000 với AMD Ryzen 7 5700U, RAM 8GB DDR4, SSD 512GB, màn hình 14 inch FHD. Thiết kế trẻ trung, webcam HD, pin 3-cell, cổng USB-A và USB-C.', 'Laptop Dell sinh viên với thiết kế hiện đại và cấu hình ổn', 70, 40, 'Dell', 'Sinh viên'),
('Dell Alienware m15 R7', 65990000, 'dell-alienware-m15-r7.jpg', 'Dell Alienware m15 R7 với Intel Core i9-12900H, RTX 3080 Ti 16GB, RAM 32GB DDR5, SSD 1TB, màn hình 15.6 inch QHD 240Hz. Hệ thống tản nhiệt Cryo-tech, bàn phím RGB AlienFX.', 'Laptop Dell gaming cao cấp với RTX 3080 Ti cực mạnh', 20, 6, 'Dell', 'Gaming');
